import ReactDOM from "react-dom";
import { useState, useEffect } from "react";
// import "./showstudent.css";
function Showstudentfees() {
  const [students, setStudents] = useState([]);

    useEffect(() => {
        //Runs only on the first render
       
  fetch("http://localhost:3000/showstudentfees", {
    method: "GET",
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
      "Access-Control-Allow-Headers": "Content-Type",
      "Access-Control-Allow-Methods": "GET, POST, PUT, DELETE, OPTIONS",
      "Access-Control-Allow-Origin": "*",
    },
    //   body: JSON.stringify(user)
  })
  
    .then((res) => res.json())
    .then((data) => {
      setStudents(data);
    })
    .catch((err) => console.log(err));
    
}, []);

  const click1 = (e) =>{
    //   console.log("hi"+e)
    var p=document.getElementsByClassName("collapse");
    // alert(p.length);
    for(var i=0;i<p.length;i++){
        p[0].style.display="none";
    }
    document.getElementById(e).style.display="block";
  }

  return (
    <div>
      <div class="container pt-4 ">
        <div class="row g-4">
          <div class="col-12">
            <div class="bg-secondary rounded h-100 p-4">
              <h6 class="mb-4">Responsive Table</h6>
              <div class="table-responsive">
              <div id="accordion">
  <div class="">

                <table class="table">
                  <thead>
                    <tr>
                      <th scope="col">ID</th>
                      <th scope="col">Name</th>
                      <th scope="col">Email</th>
                      <th scope="col">Course</th>
                      <th scope="col">Date of joining</th>
                      <th scope="col">Fees</th>
                      <th scope="col">Due MONTH</th>
                      <th scope="col">Details</th>
                    </tr>
                  </thead>
                  <tbody id="show">
                    {students.map((data) => (
                      <>
                        <tr>
                          <th scope="row">2</th>
                          <td>{data.name}</td>
                          <td>{data.email}</td>
                          <td></td>
                          <td>{data.month}</td>
                          <td>456</td>
                          <td>Member</td>
                          <td>
                            <button
                              class="btn btn-success  collapsed"
                              type="button"
                              data-toggle="collapse"
                              data-target={"#"+data.email}
                              aria-expanded="false"
                              aria-controls="collapseExample"
                            //   onClick={(e)=>click1(data.email)}
                            >
                              view profile
                            </button>
                          </td>
                        </tr>
                        <tr>
                          <td colSpan="8">
                            <div class="collapse "data-parent="#accordion" id={data.email}>
                              



                            to get the result that you can see in the preview selection





                            </div>
                          </td>
                        </tr>
                      </>
                    ))}
                  </tbody>
                </table>
              </div>
              </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Showstudentfees;
