import Button from 'react-bootstrap/Button';
import Placeholder from 'react-bootstrap/Placeholder';
import Form from 'react-bootstrap/Form';
// name email date id course
function Addstudent() {

  function register()
  {
    const[name,setname]=useState('')
    const[email,setphone]=useState('')
    const[new1,setnew1]=useState('')
    let [mes,setmes]=useState('')
    const[new0,setnew0]=useState('')
    const[delname,setdel]=useState('')
  
    const [result, setResult] = useState('');

    const clickme = (e) => {
        // alert(name+phone);
    e.preventDefault();
        const user = { username: name, phone: phone };
        console.log("hi"+user.username);

        fetch("http://localhost:3000/adduser", {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
            "Access-Control-Allow-Headers": "Content-Type",
            "Access-Control-Allow-Methods": "GET, POST, PUT, DELETE, OPTIONS",
            "Access-Control-Allow-Origin": "*"
          },
          body: JSON.stringify(user)
        })
          .then(res => res.json())
          .then(data => {
    
            console.log(data);
            window.location.replace("login.js");

            if(data.affectedRows>0){
                // setmes("registration successful")
                // mes1="registration successful";
                alert("success");
                //console.log(mes1);
                document.getElementById("show").innerHTML="registration successful";
            }
          })
          .catch(err => console.log(err));
    
      }
    
  

  }

  return (
    <div  >
      <div class="container-fluid pt-4 px-4 " >
        <div class="">

          <div class="">
            <div class="bg-secondary rounded h-100 p-4">
              <h6 class="mb-4">Student Registration</h6>
              <form>
                <div class="mb-3">
                  <label for="exampleInputEmail1" class="form-label">Email Id </label>
                  <input type="email" class="form-control" id="exampleInputEmail1"
                    aria-describedby="emailHelp" />
                  {/* <div id="emailHelp" class="form-text">We'll never share your email with anyone else.
                  </div> */}
                </div>
                <div class="mb-3">
                  <label for="exampleInputPassword1" class="form-label">Name</label>
                  <input type="text" class="form-control" id="exampleInputText1" />
                </div>
                <div  class="mb-3">
                  <label for="exampleInputPassword1" class="form-check">Courses</label>
                  <br />
                  <input class="form-check-input" type="checkbox" value="" id="flexCheckIndeterminate" />
                  <label class="form-check-label" for="flexCheckIndeterminate">
                    JAVASCRIPT
                  </label>
                  <br />

                  <input class="form-check-input" type="checkbox" value="" id="flexCheckIndeterminate" />
                  <label class="form-check-label" for="flexCheckIndeterminate">
                    PHP
                  </label>
                  <br />
                  <input class="form-check-input" type="checkbox" value="" id="flexCheckIndeterminate" />
                  <label class="form-check-label" for="flexCheckIndeterminate">
                    C++
                  </label>
                  <br></br>
                  <input class="form-check-input" type="checkbox" value="" id="flexCheckIndeterminate" />
                  <label class="form-check-label" for="flexCheckIndeterminate">
                    PYTHON
                  </label>
                </div>
                <button type="submit" class="btn btn-primary">Sign in</button>
              </form>
            </div>
          </div>
        </div>
      </div>

    </div>
  );
}

export default Addstudent;
