        setdata(data);
        console.log(data.batch);
        setbatch(data.batch);

        // setFilteredList(data);
        // setStudents(data);
      })
      .catch((err) => console.log(err));
    // });
  }, []);


    function logout(){
        localStorage.setItem("app",0);
        localStorage.setItem("sora",0);

        // window.rel
        window.location.reload();
    }
   
    
    return (
        <div
            className="student"
            data-spy="scroll"
            data-target=".navbar"
            data-offset="40"
            id="home"
        >
            <div
